import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../Card.css';


const words = [
  { id: 1, word: 'apple', translation: 'яблоко' },
  { id: 2, word: 'book', translation: 'книга' },
  { id: 3, word: 'sun', translation: 'солнце' },
];

function Card() {
  const { id } = useParams();
  const navigate = useNavigate();
  const currentId = parseInt(id);
  const word = words.find(w => w.id === currentId);
  const [showTranslation, setShowTranslation] = useState(false);
  if (!word) {
    return <div>Слово не найдено</div>;
  }
  const currentIndex = words.findIndex(w => w.id === currentId);

 // Функции переключения
 const goPrev = () => {
  if (currentIndex > 0) {
    navigate(`/card/${words[currentIndex - 1].id}`);
    setShowTranslation(false); // скрываем перевод при переключении
  }
};

const goNext = () => {
  if (currentIndex < words.length - 1) {
    navigate(`/card/${words[currentIndex + 1].id}`);
    setShowTranslation(false); // скрываем перевод при переключении
  }
};

  return (
    <div className="word-card">
      <div className="card-content">
        <p className="english-word"><strong>Слово:</strong> {word.word}</p>

        {!showTranslation ? (
  <button
    className="word-list-button"
    onClick={() => setShowTranslation(true)}
  >
    Показать перевод
  </button>
) : (
  <>
    <p className="translation"><strong>Перевод:</strong> {word.translation}</p>
    <button
      className="word-list-button"
      onClick={() => setShowTranslation(false)}
      style={{ marginTop: '10px' }}
    >
      Скрыть перевод
    </button>
  </>
)}
<div style={{ marginTop: '20px', display: 'flex', justifyContent: 'space-between' }}>
          <button
            className="word-list-button"
            onClick={goPrev}
            disabled={currentIndex === 0}
          > ← Назад </button>
          <button
            className="word-list-button"
            onClick={goNext}
            disabled={currentIndex === words.length - 1}
          >
            Вперед →
          </button>
          </div>
      </div>
    </div>
  );
}

export default Card;
