import React, { createContext, useState } from 'react';

export const WordContext = createContext();

const initialWords = [
  { id: 1, word: 'apple', translation: 'яблоко' },
  { id: 2, word: 'book', translation: 'книга' },
  { id: 3, word: 'sun', translation: 'солнце' },
];

export function WordContextProvider({ children }) {
  const [words, setWords] = useState(() => {
    const saved = localStorage.getItem('words');
    return saved ? JSON.parse(saved) : initialWords;
  }); 



  const addWord = async (newWord) => {
    setWords(prev => [...prev, { id: Date.now(), ...newWord }]);
  };

  const updateWord = async (id, updatedWord) => {
    setWords(prev => prev.map(w => (w.id === id ? { ...w, ...updatedWord } : w)));
  };

  const deleteWord = async (id) => {
    setWords(prev => prev.filter(w => w.id !== id));
  };

  return (
    <WordContext.Provider
      value={{ words, error, fetchWords, addWord, updateWord, deleteWord }}
    >
      {children}
    </WordContext.Provider>
  );
}
