import React, { createContext, useState, useEffect } from 'react';

export const WordContext = createContext();

export function WordContextProvider({ children }) {
    const [words, setWords] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

  // Загрузка слов 
    useEffect(() => {
        fetchWords();
    }, []);

    const fetchWords = async () => {
        try {
        setLoading(true);
        const response = await fetch('http://localhost:3001/words'); // Замените на ваш реальный адрес
        if (!response.ok) {
        throw new Error('Ошибка загрузки слов');
        }
        const data = await response.json();
            setWords(data);
        } catch (err) {
        setError(err.message || 'Неизвестная ошибка');
        } finally {
        setLoading(false);
    }
    };

    const addWord = async (newWord) => {
        try {
            const response = await fetch('http://localhost:3001/words', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(newWord),
    });
    const savedWord = await response.json();
        setWords(prev => [...prev, savedWord]);
    } catch (err) {
        setError('Ошибка добавления слова');
    }
    };

    const updateWord = async (id, updatedWord) => {
        try {
            const response = await fetch(`http://localhost:3001/words/${id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(updatedWord),
        });
            const data = await response.json();
                setWords(prev =>
            prev.map(word => (word.id === id ? data : word))
        );
    } catch (err) {
        setError('Ошибка обновления слова');
    }
};

    const deleteWord = async (id) => {
    try {
        await fetch(`http://localhost:3001/words/${id}`, {
        method: 'DELETE',
    });
        setWords(prev => prev.filter(word => word.id !== id));
    } catch (err) {
        setError('Ошибка удаления слова');
    }
    };

    return (
    <WordContext.Provider
        value={{
        words,
        loading,
        error,
        addWord,
        updateWord,
        deleteWord,
        fetchWords,
        }}
    >
        {children}
    </WordContext.Provider>
);
}
