import React, { useContext, useState } from 'react';
import { WordContext } from './WordContext';
import ErrorDisplay from './ErrorDisplay';

function WordList() {
  const { words, loading, error, addWord, updateWord, deleteWord } = useContext(WordContext);

  const [newWordText, setNewWordText] = useState('');

  const handleAdd = () => {
    if (!newWordText.trim()) return;
    addWord({ text: newWordText.trim() });
    setNewWordText('');
  };

  // Пример обновления слова:
  const handleUpdate = (id) => {
    const updatedText = prompt('Введите новое слово:');
    if (updatedText) {
      updateWord(id, { text: updatedText });
    }
  };

  if (loading) return <div>Загрузка...</div>;

  return (
    <div>
      <h2>Список слов</h2>
      {error && <ErrorDisplay message={error} />}
      <input
        type="text"
        value={newWordText}
        onChange={e => setNewWordText(e.target.value)}
        placeholder="Новое слово"
      />
      <button onClick={handleAdd}>Добавить</button>

      <ul>
        {words.map(word => (
          <li key={word.id}>
            {word.text}{' '}
            <button onClick={() => handleUpdate(word.id)}>Изменить</button>{' '}
            <button onClick={() => deleteWord(word.id)}>Удалить</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default WordList;
