import { Link, useNavigate } from 'react-router-dom';
import { useStores } from '../stores/StoreContext';
import { observer } from 'mobx-react-lite';
import '../Header.css';
import logo from '../assets/logo.png';  

  const Header = observer(() => {
  const { wordStore } = useStores();
  const { words } = wordStore;
  const navigate = useNavigate();

  const handleHomeClick = () => {
    navigate('/'); // Переход на главную (WordList)
  };

  const handleCardsClick = () => {
    if (words.length > 0) {
      navigate(`/card/${words[0].id}`); // Переход на первую карточку
    } else {
      navigate('/'); // Если слов нет, на главную
    }
  };

  return (
    <header className="app-header">
      <div className="logo-container">
        <Link to="/" className="logo-link">
          <img src={logo} alt="Логотип" className="logo" />
        </Link>
        <h1 className="site-title">Словарь</h1>
      </div>
      <nav className="nav-buttons">
        <button onClick={handleHomeClick} className="nav-link">
          Главная
        </button>
        <button onClick={handleCardsClick} className="nav-link">
          Карточки
        </button>          
      </nav>
    </header>
  );
});
