import React, { createContext, useState, useEffect } from 'react';

// Создаем контекст
export const WordContext = createContext();

export function WordContextProvider({ children }) {
  const [words, setWords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Функция для загрузки слов с сервера
  const fetchWords = async () => {
    setLoading(true);
    setError(null);
    try {

      const data = [
        { id: 1, text: 'apple' },
        { id: 2, text: 'banana' },
      ];
      // имитация задержки
      await new Promise(res => setTimeout(res, 500));
      setWords(data);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

     /* const response = await fetch('https://your-api.com/api/words');
      if (!response.ok) throw new Error(`Ошибка загрузки: ${response.status}`);
      const data = await response.json();
      setWords(data);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }; */

  // Загрузка данных при монтировании (componentDidMount)
  useEffect(() => {
    fetchWords();
  }, []);

  // Добавление слова
  const addWord = async (newWord) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('https://your-api.com/api/words', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newWord),
      });
      if (!response.ok) throw new Error('Ошибка добавления');
      const createdWord = await response.json();
      setWords(prev => [...prev, createdWord]);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  // Изменение слова
  const updateWord = async (id, updatedWord) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(``, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedWord),
      });
      if (!response.ok) throw new Error('Ошибка обновления');
      const data = await response.json();
      setWords(prev => prev.map(w => (w.id === id ? data : w)));
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  // Удаление слова
  const deleteWord = async (id) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`https://your-api.com/api/words/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Ошибка удаления');
      setWords(prev => prev.filter(w => w.id !== id));
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <WordContext.Provider
      value={{
        words,
        loading,
        error,
        fetchWords,
        addWord,
        updateWord,
        deleteWord,
      }}
    >
      {children}
    </WordContext.Provider>
  );
}
