import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const words = [
    { id: 1, word: 'apple', translation: 'яблоко' },
    { id: 2, word: 'book', translation: 'книга' },
    { id: 3, word: 'sun', translation: 'солнце' },
];

function Card() {
    const { id } = useParams();
    const word = words.find(w => w.id === parseInt(id));
    const [showTranslation, setShowTranslation] = useState(false);

    if (!word) {
        return <div>Слово не найдено</div>;
    }

    return (
        <div className="word-card" style={{ padding: '20px' }}>
            <h2>Карточка слова</h2>
            <p><strong>Слово:</strong> {word.word}</p>

        {!showTranslation ? (
            <button
                className="word-list-button"
                onClick={() => setShowTranslation(true)}
            > Показать перевод </button>
        ) : (
        <p><strong>Перевод:</strong> {word.translation}</p>
        )}

        <div style={{ marginTop: '20px' }}>
            <Link to="/" className="word-list-button">Назад к списку</Link>
        </div>
        </div>
    );
}

export default Card;
